#include<iostream>
#include<algorithm>
#include<queue>

using namespace std;

int h, w, map[1001][1001];

struct pos {
	int x, y;
};

std::queue<pair<pos, int> > que;

int check(int _X, int _Y) {
	int count = 0;
	for (int i = -1;i <= 1;i++) {
		for (int j = -1;j <= 1;j++) {
			if (map[_X + i][_Y + j] == -1)count++;
		}
	}
	return count;
}

bool Cl(int _X, int _Y) { return check(_X, _Y) >= map[_X][_Y]; };

void Break(int _x, int _y, int _wave) {
	pos p;
	p.x = _x, p.y = _y;
	que.push(make_pair(p, _wave));
}

int main() {
	cin >> h >> w;
	for (int i = 1;i <= h;i++) {
		for (int j = 1;j <= w;j++) {
			char c;
			cin >> c;
			if (c == '.')map[i][j] = -1;
			else map[i][j] = c - '0';
		}
	}
	for (int i = 2;i <= h - 1;i++) {
		for (int j = 2;j <= w - 1;j++) {
			if (Cl(i, j) && map[i][j] != -1)map[i][j] = 0, Break(i, j, 1);
			else if (map[i][j] != -1) {
				map[i][j] -= check(i, j);
			}
		}
	}
	int big = 0;
	while (que.size()) {
		pair<pos, int> P = que.front();
		que.pop();
		pos po;
		po = P.first;
		if (map[po.x][po.y] == -1)continue;
		big = std::max(big, P.second);
		map[po.x][po.y] = -1;
		for (int i = -1;i <= 1;i++) {
			for (int j = -1;j <= 1;j++) {
				pos tep;
				tep.x = po.x + i, tep.y = po.y + j;
				if (map[tep.x][tep.y] > 0)map[tep.x][tep.y]--;
				if (map[tep.x][tep.y] == 0)que.push(make_pair(tep, P.second+1));
			}
		}
	}
	cout << big << endl;
	return 0;
}

