      if (!inside(ny, nx, h, w)) continue;
      if (field[ny][nx] != '.') continue;
      cnt[i][j]++;
    }
    if (cnt[i][j] >= field[i][j] - '0') {
      q.emplace(i, j);
    }
  }
  if (q.empty()) {
    print(0);
    return 0;
  }
  int ans = 1;
  while (q.size()) {
    //debug(q.size());
    queue<pii> qq;
    while (q.size()) {
      pii t = q.front(); q.pop();
      REP(i, 8) {
        int ny = t.first + dy[i], nx = t.second + dx[i];
        if (!inside(ny, nx, h, w)) continue;
        if (field[ny][nx] == '.') continue;
        cnt[ny][nx]++;
        if (cnt[ny][nx] == field[ny][nx] - '0') {
          qq.emplace(ny, nx);
        }
      }
    }
    if (qq.size()) ans++;
    q = qq;
  }
  print(ans);

  return 0;
}

