#include <bits/stdc++.h>
using namespace std;


struct State {
  int i, j, t;
};

const int INF = 1<<28;
const int MAXN = 1001;
int H, W;
char C[MAXN][MAXN];
int G[MAXN][MAXN];

int main() {
  while(cin >> H >> W) {
    for(int i = 0; i < H; ++i) {
      for(int j = 0; j < W; ++j) {
        cin >> C[i][j];
      }
    }
    memset(G, 0, sizeof(G));
    queue<State> que;
    for(int i = 0; i < H; ++i) {
      for(int j = 0; j < W; ++j) {
        if(!isdigit(C[i][j])) {
          G[i][j] = INF;
          continue;
        }
        for(int di = -1; di <= 1; ++di) {
          for(int dj = -1; dj <= 1; ++dj) {
            if(di == 0 && dj == 0) continue;
            int ni = i + di;
            int nj = j + dj;
            if(C[ni][nj] == '.') ++G[i][j];
          }
        }
        if(G[i][j] >= C[i][j]-'0') {
          que.push((State){i, j, 1});
        }
      }
    }
    int res = 0;
    while(que.size()) {
      const State s = que.front();
      que.pop();
      res = s.t;
      for(int di = -1; di <= 1; ++di) {
        for(int dj = -1; dj <= 1; ++dj) {
          if(di == 0 && dj == 0) continue;
          int ni = s.i + di;
          int nj = s.j + dj;
          if(G[ni][nj] >= C[ni][nj]-'0') continue;
          if(++G[ni][nj] >= C[ni][nj]-'0') {
            que.push((State){ni, nj, s.t + 1});
          }
        }
      }
    }
    cout << res << endl;
  }
  return 0;
}

