    string str;
    cin >> str;
    for (int j = 0; j < W; ++j) {
      if (str[j] == '.') {
        f[i][j] = 0;
        q.push(Q(0, P(i, j)));
      } else {
        f[i][j] = str[j] - '0';
      }
    }
  }
  cout << calc(f, q, H, W) << endl;
}

