int main(){
    int h, w;
    cin >> h >> w;
    vector<string> s(h);
    vector<vector<bool> > used(h, vector<bool>(w));
    vector<vector<int> > cnt(h, vector<int>(w));
    for(int i=0;i<h;i++){
        cin >> s[i];
    }
    queue<P> que[2];
    for(int i=1;i<h-1;i++){
        for(int j=1;j<w-1;j++){
            if(s[i][j] == '.') continue;
            for(int di=-1;di<=1;di++)for(int dj=-1;dj<=1;dj++){
                int ni = i+di, nj = j+dj;
                if(s[ni][nj] == '.') cnt[i][j]++;
            }
            if(cnt[i][j] >= s[i][j]-'0'){
                que[0].push(P(i,j));
            }
        }
    }
    bool update = true;
    int t = 0;
    while(update){
        update = false;
        while(!que[0].empty()){
            auto p = que[0].front();
            que[0].pop();
            int i = p.first, j = p.second;
            if(s[i][j] == '.') continue;
            if(used[i][j]) continue;
            if(cnt[i][j] >= s[i][j]-'0'){
                que[1].push(P(i,j));
                used[i][j] = true;
                update = true;
            }
        }
        while(!que[1].empty()){
            auto p = que[1].front();
            que[1].pop();
            int i = p.first, j = p.second;
