                if(used[ni][nj]) continue;
                cnt[ni][nj]++;
                if(cnt[ni][nj] >= s[ni][nj]-'0') que[0].push(P(ni,nj));
            }
        }
        if(update) t++;
    }
    cout << t << endl;
    return 0;
}

