main(){
	cin>>h>>w;
	r(i,h)cin>>s[i];
	queue<pair<pair<int,int>,int>>q;
	r(i,h)r(j,w)if(isdigit(s[i][j])){
		int sum=0;
		r(k,8){
			int y=i+dy[k];
			int x=j+dx[k];
			if(y<0||x<0||x>=w||y>=h)continue;
			if(s[y][x]=='.')sum++;
		}
		if(s[i][j]-'0'<=sum){
			used[i][j]=1;
			q.push(mk(mk(i,j),1));
		}
	}
	while(!q.empty()){
		vector<P>v;
		while(!q.empty()){
			pair<pair<int,int>,int>p=q.front();q.pop();
			ans=p.s;
			s[p.f.f][p.f.s]='.';
			v.push_back(P(p.f.f,p.f.s));
