      if(c == '.')
      {
        m[y][x] = -1;
      }
      else
      {
        m[y][x] = c - '0';
        up1.push(x);
        up1.push(y);
      }
    }
  }
  int ans = 0;
  while(!(up1.empty() && up2.empty()))
  {
