    queue<int> *upp1, *upp2;
    if(up1.empty())
    {
      upp2 = &up1;
      upp1 = &up2;
    }
    else
    {
      upp1 = &up1;
      upp2 = &up2;
    }
    while(!upp1->empty())
    {
      int x = upp1->front(); upp1->pop();
      int y = upp1->front(); upp1->pop();
