  if(beach[a][b-1]=='.')ret++;
  if(beach[a-1][b-1]=='.')ret++;
  if(beach[a-1][b]=='.')ret++;
  if(beach[a-1][b+1]=='.')ret++;
  if(beach[a+1][b-1]=='.')ret++;
  if(beach[a+1][b]=='.')ret++;
  if(beach[a+1][b+1]=='.')ret++;
  return ret;
}
