typedef pair< int, int > P;

int H, W;
int fld[1000][1000];
int cnt[1000][1000];
int dx[] = {-1, -1, 0, 1, 1, 1, 0, -1}, dy[] = {0, -1, -1, -1, 0, 1, 1, 1};

int main() {
	cin >> H >> W;
	queue<P> Q;
	for (int i = 0; i < H; i++) {
		string s;
		cin >> s;
		for (int j = 0; j < W; j++) {
			fld[i][j] = (s[j] == '.' ? 0 : s[j] - '0');
            if (fld[i][j] == 0) Q.push(P(i, j));
		}
	}
	int ans = -1;
    while(Q.size()) {
		ans++;
		int sz = Q.size();
		for (int i = 0; i < sz; i++) {
			P p = Q.front(); Q.pop();
			int x = p.second, y = p.first;
            for (int j = 0; j < 8; j++) {
				int nx = x + dx[j], ny = y + dy[j];
				if (nx < 0 || nx >= W || ny < 0 || ny >= H || fld[ny][nx] == 0) continue;
				cnt[ny][nx]++;
                if (cnt[ny][nx] >= fld[ny][nx]) {
					fld[ny][nx] = 0;
                    Q.push(P(ny, nx));
				}
			}
		}
	}
	cout << ans << endl;
}

