bool breakable(const vector<vector<int>> &f, int i, int j){
	int tmp  = 0;
	for(int k = 0; k < 8; k++){
		if(f[i+dx[k]][j+dy[k]] == 0)tmp++;
	}
	return tmp >= f[i][j];
}

bool iswiped(const vector<vector<int>> &f){
	for(auto a : f)for(auto e : a)if(e != 0)return false;
	return true;
}

bool innerRange(const int tar, const int l, const int r){
	return l <= tar && tar < r;
}

