using namespace std;
queue<int> que_i, que_j;

int power(int i, int j) {
	int count = 0;

	if (beach[i - 1][j] == 0) {
		count++;
	}
	if (beach[i][j + 1] == 0) {
		count++;
	}
	if (beach[i + 1][j] == 0) {
		count++;
	}
	if (beach[i][j - 1] == 0) {
		count++;
	}

	if (beach[i - 1][j + 1] == 0) {
		count++;
	}
	if (beach[i + 1][j + 1] == 0) {
		count++;
	}
	if (beach[i + 1][j - 1] == 0) {
		count++;
	}
	if (beach[i - 1][j - 1] == 0) {
		count++;
	}

	return count;
}

void around(int i, int j) {
	if (beach[i - 1][j] != 0) {
		if (beach[i - 1][j] <= power(i - 1, j)) {
			que_i.push(i - 1);
			que_j.push(j);
		}
	}
	if (beach[i][j + 1] != 0) {
		if (beach[i][j + 1] <= power(i, j + 1)) {
			que_i.push(i);
			que_j.push(j + 1);
		}
	}
	if (beach[i + 1][j] != 0) {
		if (beach[i + 1][j] <= power(i + 1, j)) {
			que_i.push(i + 1);
			que_j.push(j);
		}
	}
	if (beach[i][j - 1] != 0) {
		if (beach[i][j - 1] <= power(i, j - 1)) {
			que_i.push(i);
			que_j.push(j - 1);
		}
	}

	if (beach[i - 1][j + 1] != 0) {
		if (beach[i - 1][j + 1] <= power(i - 1, j + 1)) {
			que_i.push(i - 1);
			que_j.push(j + 1);
		}
	}
	if (beach[i + 1][j + 1] != 0) {
		if (beach[i + 1][j + 1] <= power(i + 1, j + 1)) {
			que_i.push(i + 1);
			que_j.push(j + 1);
		}
	}
	if (beach[i + 1][j - 1] != 0) {
		if (beach[i + 1][j - 1] <= power(i + 1, j - 1)) {
			que_i.push(i + 1);
			que_j.push(j - 1);
		}
	}
	if (beach[i - 1][j - 1] != 0) {
		if (beach[i - 1][j - 1] <= power(i - 1, j - 1)) {
			que_i.push(i - 1);
			que_j.push(j - 1);
		}
	}
}

