int h,w;
int t[52][52];
int u[52][52];
char str[51];
int q[3000],l,r;

void init(){
  l=r=0;
}

void push(int x){
  q[r++]=x;
}

int pop(){
  return q[l++];
}

int main(){
  init();
  scanf("%d %d",&h,&w);
  for(int i=1;i<=h;i++){
    scanf("%s",str);
    for(int j=1;j<=w;j++){
      if(str[j-1]=='.'){
        t[i][j]=0;
        u[i][j]=0;
        push(i*w+j);
      }else{
        t[i][j]=str[j-1]-'0';
      }
    }
  }

  int ans=0;
  while(l!=r){
    int x=pop();
    int i=x/w,j=x%w;
    for(int a=i-1;a<=i+1;a++){
      for(int b=j-1;b<=j+1;b++){
        t[a][b]--;
        if(t[a][b]==0){
          ans=u[a][b]=u[i][j]+1;
          push(a*w+b);
        }
      }
    }    
  }
  printf("%d\n",ans);
  return 0;
}

