int main(){
	int h,w;	cin>>h>>w;
	queue<pair<int,int>> ls;
	for(int i=0;i<h;i++){
		string s;	cin>>s;
		for(int j=0;j<w;j++){
			if(s[j]=='.')	grd[i][j]=-1;
			else{
				grd[i][j]=s[j]-'0';
				ls.push(make_pair(i,j));
			}
		}
	}
	int ans=0;
	while(true){
		//cout<<ans<<endl;
