int ym[] = { -1,-1,-1,0,0,0,1,1,1 };

void Updata(int h,int w) {
	for (int i = 1; i <= h; ++i) {
		for (int i2 = 1; i2 <= w; ++i2) {
			map[i][i2] = map2[i][i2];
		}
	}

