#define INF (1 << 29)

const int dx[8] = { -1, 0, 1, -1, 1, -1, 0, 1 };
const int dy[8] = { -1, -1, -1, 0, 0, 1, 1, 1 };

int H, W;

int main()
{
	cin >> H >> W;

	vector<vector<char> > D(H, vector<char>(W));
	vector<vector<int> > dp(H, vector<int>(W, INF));

	for (int i = 0; i < H; i++)
	{
		for (int j = 0; j < W; j++)
		{
			cin >> D[i][j];

			if (D[i][j] == '.')
			{
				dp[i][j] = 0;
			}
		}
	}

	queue<pair<int, int> > que;
	queue<int> q2;

	for (int i = 0; i < H; i++)
	{
		for (int j = 0; j < W; j++)
		{
			if (dp[i][j] != 0)
			{
				int count = 0;

				for (int dir = 0; dir < 8; dir++)
				{
					if (0 <= i + dy[dir] && i + dy[dir] < H && 0 <= j + dx[dir] && j + dx[dir] < W)
					{
						if (dp[i + dy[dir]][j + dx[dir]] == 0)
						{
							count++;
						}
					}
				}

				if (count >= D[i][j] - 48)
				{
					pair<int, int> p1;

					p1.first = j;
					p1.second = i;

					que.push(p1);
					q2.push(1);
				}
			}
		}
	}

	while (!que.empty())
	{
		pair<int, int> p1 = que.front(); que.pop();

		int dist = q2.front(); q2.pop();

		int x = p1.first;
		int y = p1.second;
		int count = 0;

		for (int dir = 0; dir < 8; dir++)
		{
			if (0 <= y + dy[dir] && y + dy[dir] < H && 0 <= x + dx[dir] && x + dx[dir] < W)
			{
				if (dp[y + dy[dir]][x + dx[dir]] < dist)
				{
					count++;
				}
			}
		}

		if (count >= D[y][x] - 48)
		{
			dp[y][x] = dist;

			pair<int, int> p2;

			for (int dir = 0; dir < 8; dir++)
			{
				if (0 <= y + dy[dir] && y + dy[dir] < H && 0 <= x + dx[dir] && x + dx[dir] < W)
				{
					if (dp[y + dy[dir]][x + dx[dir]] == INF)
					{
						p2.first = x + dx[dir];
						p2.second = y + dy[dir];

						que.push(p2);
						q2.push(dist + 1);
					}
				}
			}
		}
	}

	int Ans = 0;

	for (int i = 0; i < H; i++)
	{
		for (int j = 0; j < W; j++)
		{
			if (dp[i][j] != INF)
			{
				Ans = max(Ans, dp[i][j]);
			}
		}
	}

	cout << Ans << endl;

	return 0;
}

