const int dx[] = {0, 1, 1, 1, 0, -1, -1, -1};
const int dy[] = {-1, -1, 0, 1, 1, 1, 0, -1};
bool inRange(int x, int y, int w, int h) {
	return 0 <= x && x < w && 0 <= y && y < h;
}
int H, W;
void update(vector< vector<int> >& m, queue<P>& q, int x, int y) {
	assert( m[y][x] == 0 );
	REP(i, 8) {
		int nx = x + dx[i];
		int ny = y + dy[i];
		if (inRange(nx, ny, W, H) && m[ny][nx] > 0) {
			if ( --m[ny][nx] == 0 ) {
				q.push(P(nx, ny));
			}
		}
	}
}
