		string s; cin >> s;
		REP(x, W) {
			if (isdigit(s[x])) {
				m[y][x] = s[x]-'0';
			}
			else {
				m[y][x] = 0;
			}
		}
	}
	int ans = -1;
	queue<P> Q;
	REP(y, H) {
		REP(x, W) {
			if (m[y][x] == 0) {
				Q.push(P(x, y));
			}
		}
	}
	while (1) {
		if ( Q.empty() ) break;
		queue<P> q;
		while ( !Q.empty() ) {
			P p = Q.front(); Q.pop();
			int x = p.first, y = p.second;
			update(m, q, x, y);
//			cout << x << " " << y << endl;
		}
//		cout << endl;
		Q = q;
		++ans;
	}
	cout << ans << endl;
}

