int field[1000][1000];

int check(int I, int J) {
	if (field[I][J] == 0) return false;
	int c = 0;
	for (int i = I - 1; i <= I + 1; i++) {
		for (int j = J - 1; j <= J + 1; j++) {
			if (field[i][j] == 0) c++;
		}
	}
	if (c >= field[I][J]) return true;
	else return false;
}

int main() {
	int H, W;
	cin >> H >> W;

	for (int i = 0; i < H; i++) {
		string tmp;
		cin >> tmp;
		for (int j = 0; j < W; j++) {
			if (tmp[j] == '.') field[i][j] = 0;
			else field[i][j] = tmp[j] - '0';
		}
	}

	set<pair<int, int> > changed;
	for (int i = 1; i < H-1; i++) {
		for (int j = 1; j < W-1; j++) {
			if (check(i, j)) {
				changed.insert(make_pair(i, j));
			}
		}
	}

	int ans = 0;
	while (true) {

		if (changed.size() == 0) break;
		vector<pair<int, int>> changed2;
		for (auto itr = changed.begin(); itr != changed.end(); itr++) {
			field[(*itr).first][(*itr).second] = 0;
			changed2.push_back(make_pair((*itr).first, (*itr).second));
		}
		changed.clear();

		for (auto itr = changed2.begin(); itr != changed2.end(); itr++) {
			for (int i = (*itr).first - 1; i <= (*itr).first + 1; i++) {
				for (int j = (*itr).second - 1; j <= (*itr).second + 1; j++) {
					if (check(i, j)) {
						changed.insert(make_pair(i, j));
					}
				}
			}
		}

		ans++;
	}
	cout << ans << endl;
}

