                break;
            default :
                mp[i][j]=instr[j]-'0';
                q[ans%2].push(P(i,j));
                break;
            }
        }
    }

    while(1) {
        int QCNT=q[ans%2].size();

        while(!q[ans%2].empty()) {
            P temp=q[ans%2].front(); q[ans%2].pop();
            if(check_destroy(mp,temp.first,temp.second)) dest[ans%2].push(temp);
