using namespace std;

typedef pair<int,int> P;

int H, W;

bool contains(int x, int y) {
  return 0 <= x && x < W && 0 <= y && y < H;
}

int main() {
  int dx[] = {-1, 0, 1, -1, 1, -1, 0, 1};
  int dy[] = {-1, -1, -1, 0, 0, 1, 1, 1};

  while (cin >> H >> W) {
    vector<string> t(H);

    for (int i = 0; i < H; i++) {
      cin >> t[i];
    }

    vector<vector<int> > rawCnt(H, vector<int>(W, 0));
    queue<P> open;

    for (int i = 0; i < H; i++) {
      for (int j = 0; j < W; j++) {
        if (t[i][j] != '.') continue;

        for (int k = 0; k < 8; k++) {
          int nx = j + dx[k];
          int ny = i + dy[k];

          if (!contains(nx, ny)) continue;
          if (t[ny][nx] == '.') continue;

          rawCnt[ny][nx]++;

          if (rawCnt[ny][nx] == t[ny][nx] - '0') {
            open.push(P(nx, ny));
          }
        }
      }
    }

    int ans = 0;

    while (!open.empty()) {
      queue<P> nextOpen;

      while (!open.empty()) {
        P p = open.front();
        open.pop();

        for (int k = 0; k < 8; k++) {
          int nx = p.first + dx[k];
          int ny = p.second + dy[k];

          if (!contains(nx, ny)) continue;
          if (t[ny][nx] == '.') continue;

          rawCnt[ny][nx]++;

          if (rawCnt[ny][nx] == t[ny][nx] - '0') {
            nextOpen.push(P(nx, ny));
          }
        }
      }

      open = nextOpen;
      ans++;
    }

    cout << ans << endl;
  }
}

