	flds[gn] = s[x] - '0';
	xs[gn] = x, ys[gn] = y;
	gn++;
      }
    }
  }
  //printf("gn=%d\n", gn);

  for (int y = 0; y < h; y++)
    for (int x = 0; x < w; x++)
      for (int di = 0; di < 8; di++) {
	int vx = x + dxs[di], vy = y + dys[di];
	if (vx >= 0 && vx < w && vy >= 0 && vy < h && ids[vy][vx] >= 0)
