    vi &nbr = nbrs[uy][ux];
    for (vi::iterator vit = nbr.begin(); vit != nbr.end(); vit++) {
      int &v = *vit, &vx = xs[v], &vy = ys[v];
      if (dists[vy][vx] == 0 && ++ps[v] >= flds[v]) {
	dists[vy][vx] = vd;
	if (maxd < vd) maxd = vd;
