	}
	int row,col;
};

int H,W;
int diff_row[8] = {-1,-1,-1,0,0,1,1,1},diff_col[8] = {-1,0,1,-1,1,-1,0,1};

int main(){

	scanf("%d %d",&H,&W);

	char** map = new char*[H+2];
	for(int row = 0; row <= H+1; row++){
		map[row] = new char[W+2];
	}

	for(int col = 0; col <= W+1; col++){
		map[0][col] = '.';
		map[H+1][col] = '.';
	}

	for(int row = 0; row <= H+1; row++){
		map[row][0] = '.';
		map[row][W+1] = '.';
	}

	char buf[W+1];

	for(int row = 1; row <= H; row++){
		scanf("%s",buf);
		for(int col = 1; col <= W; col++){
			map[row][col] = buf[col-1];
		}
	}

	vector<Info> broke_points;
	vector<Info> next_broke_points;

	int tmp,count,adj_row,adj_col;

	bool** deleted = new bool*[H+1];
	bool** pushed = new bool*[H+1];

	for(int row = 1; row <= H; row++){
		deleted[row] = new bool[W+1];
		pushed[row] = new bool[W+1];
		for(int col = 1; col <= W; col++){
			deleted[row][col] = false;
			pushed[row][col] = false;
		}
	}

	for(int row = 1; row <= H; row++){
		for(int col = 1; col <= W; col++){
			if(map[row][col] >= '1' && map[row][col] <= '8'){

				tmp = map[row][col] - '0';
				count = 0;

				for(int i = 0; i < 8; i++){
					adj_row = row + diff_row[i];
					adj_col = col + diff_col[i];

					if(map[adj_row][adj_col] == '.')count++;
				}

				if(count >= tmp){
					broke_points.push_back(Info(row,col));
					deleted[row][col] = true;
				}
			}
		}
	}

	if(broke_points.size() == 0){
		printf("0\n");
		return 0;
	}

	int ans = 1;
	int next_adj_row,next_adj_col;


	while(true){

		for(int i = 0; i < broke_points.size(); i++){
			map[broke_points[i].row][broke_points[i].col] = '.';

			for(int k = 0; k < 8;k++){
				adj_row = broke_points[i].row + diff_row[k];
				adj_col = broke_points[i].col + diff_col[k];

				if(deleted[adj_row][adj_col] == false && pushed[adj_row][adj_col] == false && map[adj_row][adj_col] >= '1' && map[adj_row][adj_col] <= '8'){
					tmp = map[adj_row][adj_col] - '0';
					count = 0;

					for(int p = 0; p < 8; p++){
						next_adj_row = adj_row + diff_row[p];
						next_adj_col = adj_col + diff_col[p];

						if(map[next_adj_row][next_adj_col] == '.' || deleted[next_adj_row][next_adj_col] == true){
							count++;
						}
					}

					if(count >= tmp){
						pushed[adj_row][adj_col] = true;
						next_broke_points.push_back(Info(adj_row,adj_col));
					}
				}
			}



		}
		broke_points.clear();

		if(next_broke_points.size() == 0){
			break;
		}
		ans++;

		for(int i = 0; i < next_broke_points.size(); i++){
			deleted[next_broke_points[i].row][next_broke_points[i].col] = true;
			broke_points.push_back(next_broke_points[i]);
		}
		next_broke_points.clear();
	}

	printf("%d\n",ans);

	return 0;
}

