size_t constexpr m1 = -1;
size_t constexpr di[] = {m1, m1, m1, 0, 0, 1, 1, 1};
size_t constexpr dj[] = {m1, 0, 1, m1, 1, m1, 0, 1};

using zahyo = std::pair<size_t, size_t>;

bool nyan(std::vector<std::string>& s, std::queue<zahyo>& q) {
  size_t h = s.size();
  size_t w = s[0].length();
  std::queue<zahyo> r;
  bool res = false;
  while (!q.empty()) {
    size_t i, j;
    std::tie(i, j) = q.front();
    q.pop();
    for (size_t k = 0; k < 8; ++k) {
      size_t ni = i + di[k];
      size_t nj = j + dj[k];
      if (!(ni < h && nj < w)) continue;
      if (s[ni][nj] == '.') continue;
      if (--s[ni][nj] == '0') {
        s[ni][nj] = '.';
        res = true;
        r.emplace(ni, nj);
      }
    }
  }
  q = r;
  return res;
}

int main() {
  size_t h, w;
  scanf("%zu %zu", &h, &w);

  std::vector<std::string> s(h);
  std::queue<zahyo> q;
  for (size_t i = 0; i < h; ++i) {
