    // for (auto const& si: s)
    //   fprintf(stderr, "%s\n", si.c_str());
    // fprintf(stderr, "---\n");
  }

  printf("%d\n", res);
}

#include <iostream>
#include <queue>
#include <cctype>
#include <algorithm>

using P = std::pair<std::pair<int, int>, int>;
constexpr int MAX = 1000;

int h, w;
int board[MAX][MAX];
int dx[8] = { 0,-1,-1,-1,0,1,1,1 }, dy[8] = { -1,-1,0,1,1,1,0,-1 };

int count(int x, int y) {
	int cnt = 0;
	for (int i = 0; i < 8; ++i) {
		if (!board[y + dy[i]][x + dx[i]])
			++cnt;
	}
	return cnt;
}

int solve() {
	std::queue<P> que;

	for (int i = 0; i < h; ++i) {
		for (int j = 0; j < w; ++j) {
			if (board[i][j]) {
					que.push(P(std::make_pair(j, i), 0));
			}
		}
	}

	int max = 0;

	std::vector<std::pair<int, int>> vec;

	while (!que.empty()) {
		P p = que.front(); que.pop();

		if (board[p.first.second][p.first.first] <= count(p.first.first, p.first.second)) {
			if (max < p.second) {
				for (auto& x : vec)
					board[x.second][x.first] = 0;
