#define max(a,b) ((a) > (b) ? (a) : (b))
#define min(a,b) ((a) > (b) ? (b) : (a))
char st[64][64], sts[64][64];
int H, W;

inline void copy(char a[64][64], char b[64][64]) {
	for (int i = 0; i < H; i++) {
		for (int j = 0; j < W; j++) {
			a[i][j] = b[i][j];
		}
	}
}

inline bool is_castle(char a) {
	return a != '.';
}

inline int conv(char a) {
	return a - '0';
}

inline bool state(int x, int y) {
	int c = 0;
	for (int i = max(x - 1, 0); i < min(x + 2, W); i++) {
		for (int j = max(y - 1, 0); j < min(y + 2, H); j++) {
			if (st[i][j] == '.')
				c++;
		}
	}
	return c >= conv(st[x][y]);

}

bool collapse(int x, int y) {
	if (is_castle(st[x][y]) && state(x,y)) {
		sts[x][y] = '.';
		return true;
	}
	return false;
}

bool wave() {
	bool e = false;
	copy(sts, st);
	for (int i = 0; i < H; i++) {
		for (int j = 0; j < W; j++) {
			e = collapse(i, j) ? true : e;
		}
	}
	copy(st, sts);
	return e;
}

int main(){
	scanf("%d%d", &H, &W);
	for (int i = 0; i < H; i++) {
		scanf("%s", st[i]);
	}
	for (int i = 0;;i++) {
		if (!wave()) {
			printf("%d\n", i);
			break;
		}
	}

	return 0;
}

