using namespace std;

int H, W;
int field[1010][1010];
int ctr[1010][1010];
int ctr_copy[1010][1010];

int count_emp(int x, int y) {
  int c = 0;
  for (int xs=-1; xs<=1; xs++) {
    for (int ys=-1; ys<=1; ys++) {
      if (xs == 0 && ys == 0) continue;
      if (field[x+xs][y+ys] == 0) c++;
    }
  }
  return c;
}
void re_count(int x, int y) {
  for (int xs=-1; xs<=1; xs++) {
    for (int ys=-1; ys<=1; ys++) {
      if (xs == 0 && ys == 0) continue;
      ctr[x+xs][y+ys]++;
    }
  }
}


int main() {
  cin >> H >> W;
  for (int y=0; y<H; y++) {
    for (int x=0; x<W; x++) {
      char c;
      cin >> c;
      int t = 0;
      if (c != '.') t = (int)c - (int)'0';
      field[x][y] = t;
    }
  }

  // count all
  for (int x=1; x<W-1; x++) {
    for (int y=1; y<H-1; y++) {
      ctr[x][y] = count_emp(x, y);
    }
  }

  int ans = 0;
  while (true) {
    // copy ctr
    for (int x=1; x<W-1; x++) {
      for (int y=1; y<H-1; y++) {
        ctr_copy[x][y] = ctr[x][y];
      }
    }
    bool changed = false;
    for (int x=1; x<W-1; x++) {
      for (int y=1; y<H-1; y++) {
        int t = field[x][y];
        if (t > 0 && ctr_copy[x][y] >= t) {
          field[x][y] = 0;
          re_count(x, y);
          changed = true;
        }
      }
    }
    if (!changed) break;
    ans += 1;
  }
  cout << ans << "\n";
  return 0;
}

#include <iostream>
#include <vector>
#include <queue>
#include <tuple>

void solve() {
    int h, w;
    std::cin >> h >> w;

    std::vector<std::vector<int>> dss(h, std::vector<int>(w));
    for (auto& ds : dss) {
        for (auto& d : ds) {
            char c;
            std::cin >> c;
            d = (c == '.' ? 0 : c - '0');
        }
    }

    std::queue<std::tuple<int, int, int>> que;
    for (int x = 0; x < h; ++x) {
        for (int y = 0; y < w; ++y) {
            if (dss[x][y] == 0) que.emplace(x, y, 0);
        }
    }

    int ans = 0;
    while (!que.empty()) {
        int x, y, t;
        std::tie(x, y, t) = que.front();
        que.pop();
        ans = t;

        for (int dx = -1; dx <= 1; ++dx) {
            for (int dy = -1; dy <= 1; ++dy) {
                int nx = x + dx, ny = y + dy;
                if (nx < 0 || h <= nx || ny < 0 || w <= ny) continue;
                if (--dss[nx][ny] == 0) {
                    que.emplace(nx, ny, t + 1);
                }
            }
        }
    }

    std::cout << ans << std::endl;
}

int main() {
    std::cin.tie(nullptr);
    std::cout.tie(nullptr);
    std::ios::sync_with_stdio(false);

    solve();

    return 0;
}

