typedef pair< int, int > pii;

const int dx[8] = {0,1,1,1,0,-1,-1,-1}, dy[8] = {-1,-1,0,1,1,1,0,-1};

int H, W, counter[1010][1010];
string grid[1010];

int main() {
	cin >> H >> W;
	for_(i,0,H) cin >> grid[i];

	queue< pii > cur_que;

	for_(y,0,H) for_(x,0,W) {
		counter[y][x] = -1;
		if (grid[y][x] == '.') continue;
		counter[y][x] = grid[y][x] - '0';

		for_(d,0,8) {
			int xx = x + dx[d], yy = y + dy[d];
			if (grid[yy][xx] == '.') --counter[y][x];
		}

		if (counter[y][x] == 0) cur_que.push(pii(x, y));
	}

	queue< pii > nxt_que;

	int ans = 0;

	while (!cur_que.empty()) {
		while (!cur_que.empty()) {
			pii p = cur_que.front(); cur_que.pop();
			int x = p.first, y = p.second;
			assert(grid[y][x] != '.');
			grid[y][x] = '.';

			for_(d,0,8) {
				int xx = x + dx[d], yy = y + dy[d];
				if (grid[yy][xx] == '.') continue;
				--counter[yy][xx];
				if (counter[yy][xx] == 0) nxt_que.push(pii(xx, yy));
			}
		}

		++ans;
		swap(cur_que, nxt_que);
	}

	cout << ans << endl;
}

#include<iostream>
#include<algorithm>
#include<queue>

using namespace std;

int h, w, map[1001][1001];

struct pos {
	int x, y;
};

std::queue<pair<pos, int> > que;

int check(int _X, int _Y) {
	int count = 0;
	for (int i = -1;i <= 1;i++) {
		for (int j = -1;j <= 1;j++) {
			if (map[_X + i][_Y + j] == -1)count++;
		}
	}
	return count;
}

bool Cl(int _X, int _Y) { return check(_X, _Y) >= map[_X][_Y]; };

void Break(int _x, int _y, int _wave) {
	pos p;
	p.x = _x, p.y = _y;
	que.push(make_pair(p, _wave));
}

int main() {
	cin >> h >> w;
	for (int i = 1;i <= h;i++) {
		for (int j = 1;j <= w;j++) {
			char c;
			cin >> c;
			if (c == '.')map[i][j] = -1;
			else map[i][j] = c - '0';
		}
	}
	for (int i = 2;i <= h - 1;i++) {
		for (int j = 2;j <= w - 1;j++) {
			if (Cl(i, j) && map[i][j] !=-1)map[i][j] = 0, Break(i, j, 1);
			else if(map[i][j]!=-1){
				map[i][j] -= check(i, j);
			}
		}
	}
	int big = 0;
	while (que.size()) {
		pair<pos, int> P = que.front();
		que.pop();
		pos po;
		po = P.first;
		if (map[po.x][po.y] == -1)continue;
		big = std::max(big, P.second);
		map[po.x][po.y] = -1;
		for (int i = -1;i <= 1;i++) {
			for (int j = -1;j <= 1;j++) {
				pos tep;
				tep.x = po.x + i, tep.y = po.y + j;
				if (map[tep.x][tep.y] > 0)map[tep.x][tep.y]--;
				if (map[tep.x][tep.y] == 0)que.push(make_pair(tep, ++P.second));
			}
		}
	}
	cout << big << endl;
	return 0;
}

