const int dx[] = {-1, -1, -1, 0, 0, 1, 1, 1};
const int dy[] = {-1, 0, 1, -1, 1, -1, 0, 1};

bool wave(bool flag) {
	if(que[flag].empty()) return 0;
	while(!que[flag].empty()) {
		P p = que[flag].front();
		que[flag].pop();
		rep(i, 8) {
			int nx = p.first + dx[i];
			int ny = p.second + dy[i];
			sea[ny][nx]++;
			if(fld[ny][nx] != 0 && fld[ny][nx] == sea[ny][nx]) {
				que[!flag].push(P(nx, ny));
			}
		}
	}
	return 1;
}

