int main()
{
  int H, W;
  char S[1000][1001];

  cin >> H >> W;
  for(int i = 0; i < H; i++) {
    cin >> S[i];
  }

  queue< Pii > Que;
  int ret = 0;

  for(int i = 0; i < H; i++) {
    for(int j = 0; j < W; j++) {
      if(S[i][j] == '.') Que.push(Pii(0, Pi(j, i)));
      else S[i][j] -= '0';
    }
  }
  while(!Que.empty()) {
    Pii& p = Que.front();
    ret = max(ret, p.first);
    for(int i = 0; i < 8; i++) {
