#include <bits/stdc++.h>
using namespace std;
using pii = pair<int, int>;

const int INF = 1e9;

const int dx[] = { 1, 1, 0, -1, -1, -1, 0, 1 };
const int dy[] = { 0, 1, 1, 1, 0, -1, -1, -1 };

int main()
{
	int H, W;
	cin >> H >> W;
	vector<string> a(H);
	vector<vector<int>> d(H, vector<int>(W, INF));
	deque<pii> dq;
	for (int i = 0; i < H; i++) {
		cin >> a[i];
		for (int j = 0; j < W; j++) {
			if (a[i][j] == '.') {
				a[i][j] = '0';
				dq.emplace_front(i * W + j, 0);
			}
		}
	}
	while (!dq.empty()) {
		auto p = dq.front(); dq.pop_front();
		int x = p.first / W, y = p.first % W;
		if (d[x][y] != INF) continue;
		int cnt = 0;
		for (int i = 0; i < 8; i++) {
			int tx = x + dx[i], ty = y + dy[i];
			cnt += (0 <= tx && tx < H && 0 <= ty && ty < W && d[tx][ty] < p.second);
		}
		if (cnt < a[x][y] - '0') continue;
		d[x][y] = p.second;
		for (int i = 0; i < 8; i++) {
			int tx = x + dx[i], ty = y + dy[i];
			if (tx < 0 || H <= tx || ty < 0 || W <= ty || d[tx][ty] != INF) continue;
			int cnt = 0;
			for (int j = 0; j < 8; j++) {
				int ttx = tx + dx[j], tty = ty + dy[j];
				cnt += (0 <= ttx && ttx < H && 0 <= tty && tty < W && d[ttx][tty] < d[x][y]);
			}
			if (cnt >= a[tx][ty] - '0') {
				dq.emplace_front(tx * W + ty, d[x][y]);
			}
			else {
				dq.emplace_back(tx * W + ty, d[x][y] + 1);
			}
		}
	}
	int res = 0;
	for (int i = 0; i < H; i++) {
		for (int j = 0; j < W; j++) {
			if (d[i][j] != INF) {
				res = max(res, d[i][j]);
			}
		}
	}
	cout << res << endl;
	return 0;
}

