int countaround(vector<vector<int> >& mp, int h, int w) {
    int cnt=0;
    int dh[8]={-1,-1,-1,0,1,1,1,0};
    int dw[8]={-1,0,1,1,1,0,-1,-1};

    for(int i=0; i<8; ++i) {
        if(h+dh[i]>=0 && h+dh[i]<(int)mp.size() && w+dw[i]>=0 && w+dw[i]<(int)mp[0].size() && mp[h+dh[i]][w+dw[i]]==0) ++cnt;
    }
    return cnt;
}

bool checkcount(vector<vector<int> >& mp, int h, int w) {
    return countaround(mp,h,w)>=mp[h][w];
}

typedef pair<int,int> P;

int main()
{
    int H,W;
    cin >> H >> W;

    vector<vector<int> > mp,updt;

    mp.resize(H);
    updt.resize(H);

    for(int i=0; i<H; ++i) {
        string instr;
        cin >> instr;

        mp[i].resize(W);
        updt[i].resize(W);
        for(int j=0; j<W; ++j) {
            if(instr[j]=='.') mp[i][j]=0;
            else mp[i][j]=instr[j]-'0';
            updt[i][j]=0;
        }
    }

    queue<P> q[2];
    int idxq=0;

    for(int i=0; i<H; ++i) {
        for(int j=0; j<W; ++j) {
            if(checkcount(mp,i,j)) {
                q[idxq].push(P(i,j));
                updt[i][j]=1;
            }
        }
    }

    int dh[8]={-1,-1,-1,0,1,1,1,0};
    int dw[8]={-1,0,1,1,1,0,-1,-1};

    while(true) {
        while(!q[idxq%2].empty()) {
            P p=q[idxq%2].front(); q[idxq%2].pop();
            mp[p.first][p.second]=0;
            for(int i=0; i<8; ++i) {
                if(p.first+dh[i]>=0 && p.first+dh[i]<(int)mp.size() && p.second+dw[i]>=0 && p.second+dw[i]<(int)mp[0].size() &&
                   updt[p.first+dh[i]][p.second+dw[i]]==0 && checkcount(mp,p.first+dh[i],p.second+dw[i])) {
                    q[(idxq+1)%2].push(P(p.first+dh[i],p.second+dw[i]));
                    updt[p.first+dh[i]][p.second+dw[i]]=1;
                }
            }
        }

        ++idxq;
        if(q[idxq%2].empty()) break;
    }

    cout << idxq << endl;

    return 0;
}

