const int dx[] = {-1, -1, -1, 0, 1, 1, 1, 0};
const int dy[] = {-1, 0, 1, 1, 1, 0, -1, -1};
const int INF = 1 << 28;

int h, w;
int grid[1000][1000];
int br[1000][1000];

bool check(int x, int y, int t)
{
	int cnt = 0;
	for (int dir = 0; dir < 8; dir++){
		int nx = x + dx[dir];
		int ny = y + dy[dir];
		if (br[nx][ny] <= t) cnt++;
	}
	return cnt >= grid[x][y];
}

int main()
{
	cin >> h >> w;
	for (int i = 0; i < h; i++){
		for (int j = 0; j < w; j++){
			char c;
			cin >> c;
			if (c != '.'){
				grid[i][j] = c - '0';
				br[i][j] = INF;
			}
		}
	}

	typedef tuple<int, int, int> Tp;
	queue<Tp> q;

	for (int i = 0; i < h; i++){
		for (int j = 0; j < w; j++){
			if (br[i][j] < INF) continue;
			if (check(i, j, 0)){
				br[i][j] = 1;
				q.push(Tp(i, j, 1));
			}
		}
	}

	int res = 0;
	while (q.size()){
		int x, y, t;
		tie(x, y, t) = q.front(); q.pop();
		for (int dir = 0; dir < 8; dir++){
			int nx = x + dx[dir];
			int ny = y + dy[dir];
			if (br[nx][ny] < INF) continue;
			if (check(nx, ny, t)){
				br[nx][ny] = t + 1;
				q.push(Tp(nx, ny, t + 1));
			}
		}
		res = t;
	}

	cout << res << endl;

	return 0;
}

