int h,w;
int map[1001][1001];
int dx[9]={1,1,1,0,0,0,-1,-1,-1},dy[9]={1,0,-1,1,0,-1,1,0,-1};
int qx[1000001],qy[1000001],ql[1000001];
int head=0,tail=0;
void BFS(int y,int x,int len){
	map[y][x]--;
	for(int i=0;i<9;i++){
		if(x+dx[i]>=0&&x+dx[i]<w&&y+dy[i]>=0&&y+dy[i]<h){
			map[y+dy[i]][x+dx[i]]--;
			if(map[y+dy[i]][x+dx[i]]==0){
				qx[tail]=x+dx[i],qy[tail]=y+dy[i];
				ql[tail]=len+1;
				tail++;
			}
		}
	}
}
int main(){
	cin>>h>>w;
	char z;
	for(int i=0;i<h;i++){
		for(int j=0;j<w;j++){
			cin>>z;
			if(z=='.'){
				map[i][j]=0;
				qy[tail]=i,qx[tail]=j;
				ql[tail]=0;
				tail++;
			}
			else map[i][j]=z-'0';
		}
	}
	int ans=0;
	while(true){
		if(head==tail)break;
		ans=max(ans,ql[head]);
		BFS(qy[head],qx[head],ql[head]);
		head++;
	}
	cout<<ans<<endl;
	return 0;
}

