int main()
{
  cin >> h >> w;
  for(int i = 0; i < h; i++)
    for(int j = 0; j < w; j++){
      char in;
      cin >> in;
      if(in == '.')
	field[i][j] = 0;
      else{
	field[i][j] = in - '0';
	broken[i][j] = INF;
      }
    }

  for(int i = 1; i < h - 1; i++)
    for(int j = 1; j < w - 1; j++){
      if(!(broken[i][j] <= 1)){
	int many = 0;
	for(int k = 0; k < 8; k++)
	  if(broken[i + dx[k]][j + dy[k]] < 1)
	    many++;
