	  broken[i][j] = 1;
	}
      }
    }

  while(!sr.empty()){
    ans++;

    int sz = sr.size();
    for(int i = 0; i < sz; i++){
      PII now = sr.front();
      sr.pop();

      for(int j = 0; j < 8; j++){
	PII next = MP(now.first + dx[j], now.second + dy[j]);

	if(!(broken[next.first][next.second] <= ans + 1)){
