	  for(int k = 0; k < 8; k++)
	    if(broken[next.first + dx[k]][next.second + dy[k]] < ans + 1)
	      many++;

	  if(many >= field[next.first][next.second]){
	    sr.push(MP(next.first, next.second));
	    broken[next.first][next.second] = ans + 1;
	  }
	}
      }

    }
  }
  cout << ans << endl;
  return 0;
}

