							if(dt[i+dl[k]][j+dl[l]]=='.')hoge++;
						}
					}
					if(hoge>=(int)(dt[i][j]-'0')){
						alt[i][j]=true;
					}
				}
			}
		}
		rep(i,h){
			rep(j,w){
				if(alt[i][j]){
					check=true;
					dt[i][j]='.';
				}
			}
		}
		if(check)cnt++;
	}
	cout<<cnt<<endl;
	return 0;
}

