                        if (i+k>0 and i+k<h-1 and j+l>0 and j+l<w-1) {
                            ch[(i+k)*w+j+l]++;
                        }
                    }
                }
            }else{
                area[i*w+j]=(int)hoge-(int)'0';
            }
        }
    }
    rep(i, h){
        rep(j, w){
            if (area[i*w+j]!=-1 and ch[i*w+j]>=area[i*w+j]) {
                lis.push({i,j});
            }
        }
    }
    int ans = 0;
    int flag = 1;
    while (flag > 0) {
        vector<int> oldc = ch;
