            if (ans==0) ans = 1;
            int i = lis.front().first;
            int j = lis.front().second;
            lis.pop();
            area[i*w+j]=-1;
            for (int k=-1; k<2; k++) {
                for (int l=-1; l<2; l++) {
                    if(k==0 and l==0) continue;
                    if (i+k>0 and i+k<h-1 and j+l>0 and j+l<w-1) {
                        ch[(i+k)*w+j+l]++;
                        if (area[(i+k)*w+j+l]!=-1 and ch[(i+k)*w+j+l]==area[(i+k)*w+j+l]) {
                            lis.push({i+k,j+l});
                            flag++;
                        }
                    }
                }
            }
        }
        if(flag > 0) ans++;
    }
    cout << ans << endl;
}

