#include<bits/stdc++.h>
#define mp make_pair
using namespace std;
typedef pair<int, int> pii;
typedef pair<int, pii> piii;//cnt, coodinate
string grid[1001];
int neiber[1001][1001],
  dx[] = {0, 1, 1,  1,  0, -1, -1, -1},
  dy[] = {1, 1, 0, -1, -1, -1,  0,  1};
int w, h;
inline bool renge(int x, int y){
  return (0 <= x && x < w &&
          0 <= y && y < h);
}
bool del(int x, int y){
  int cnt = 0;
  for (int i = 0; i < 8; i++) {
    int nx = x + dx[i], ny = y + dy[i];
    if(!renge(nx, ny))continue;
    if(grid[ny][nx] == '.')cnt++;
  }
  char tmp = grid[y][x];
  neiber[y][x] = cnt;
  return cnt >= atoi(&tmp);
}

int main(int argc, char *argv[]){
  queue<piii> que;
  cin >> h >> w;
  for (int i = 0; i < h; i++)
    cin >> grid[i];

  for (int i = 0; i < h; i++) {
    for (int j = 0; j < w; j++) {
      if(grid[i][j] != '.' && del(j, i))
        que.push(mp(1, mp(j, i)));
    }
  }

  int ans = 0;
  while(!que.empty()){
    int cnt = que.front().first;
    int x = que.front().second.first;
    int y = que.front().second.second;
    que.pop();
    if(grid[y][x] == '.')continue;
    grid[y][x] = '.';
    ans = cnt;
    for (int i = 0; i < 8; i++) {
      int nx = x + dx[i], ny = y + dy[i];
      if(!renge(nx, ny))continue;
      char tmp = grid[ny][nx];
      neiber[ny][nx]++;
      if(neiber[ny][nx] >= atoi(&tmp))
        que.push(mp(cnt + 1, mp(nx, ny)));
    }
  }
  std::cout << ans << std::endl;
  return 0;
}

#include<iostream>
#include<algorithm>
#include<queue>

using namespace std;

int h, w, map[1001][1001];

struct pos {
	int x, y;
};

std::queue<pair<pos, int>> que;

int check(int _X, int _Y) {
	int count = 0;
	for (int i = -1;i <= 1;i++) {
		for (int j = -1;j <= 1;j++) {
			if (map[_X + i][_Y + j] == -1)count++;
		}
	}
	return count;
}

bool Cl(int _X, int _Y) { return check(_X, _Y) >= map[_X][_Y]; };

void Break(int _x, int _y, int _wave) {
	pos p;
	p.x = _x, p.y = _y;
	que.push(make_pair(p, _wave));
}

int main() {
	cin >> h >> w;
	for (int i = 1;i <= h;i++) {
		for (int j = 1;j <= w;j++) {
			char c;
			cin >> c;
			if (c == '.')map[i][j] = -1;
			else map[i][j] = c - '0';
		}
	}
	for (int i = 2;i <= h - 1;i++) {
		for (int j = 2;j <= w - 1;j++) {
			if (Cl(i, j) && map[i][j] !=-1)map[i][j] = 0, Break(i, j, 1);
			else if(map[i][j]!=-1){
				map[i][j] -= check(i, j);
			}
		}
	}
	int big = 0;
	while (que.size()) {
		pair<pos, int> P = que.front();
		que.pop();
		pos po;
		po = P.first;
		if (map[po.x][po.y] == -1)continue;
		big = std::max(big, P.second);
		map[po.x][po.y] = -1;
		for (int i = -1;i <= 1;i++) {
			for (int j = -1;j <= 1;j++) {
				pos tep;
				tep.x = po.x + i, tep.y = po.y + j;
				if (map[tep.x][tep.y] > 0)map[tep.x][tep.y]--;
				if (map[tep.x][tep.y] == 0)que.push(make_pair(tep, ++P.second));
			}
		}
	}
	cout << big << endl;
	return 0;
}

