using namespace std;
typedef pair<int, int> PII;
typedef vector<PII> VP;
typedef VP::iterator VPI;

int h, w, ans = 0, near[1000][1000];
char beach[1000][1000];
VP broken1, broken2;
VPI p;
int dx[8] = {-1, -1, -1, 0, 0, 1, 1, 1};
int dy[8] = {-1, 0, 1, -1, 1, -1, 0, 1};

int main()
{
  cin >> h >> w;
  for(int i = 0; i < h; i++){
    for(int j = 0; j < w; j++) cin >> beach[i][j];
  }

  for(int i = 0; i < h; i++){
    for(int j = 0; j < w; j++){
      if(beach[i][j] == '.') near[i][j] = -1;
      else{
	for(int k = 0; k < 8; k++) if(beach[i + dx[k]][j + dy[k]] == '.') near[i][j]++;
	if(beach[i][j] - '0' <= near[i][j]){
	  broken2.push_back(make_pair(i, j));
	  near[i][j] = -1;
	}
      }
    }
  }

  while(!broken2.empty()){
    ans++;
    broken1 = broken2;
    broken2.clear();
    for(p = broken1.begin(); p != broken1.end(); p++){
      for(int i = 0; i < 8; i++){
	PII next = make_pair(p->x + dx[i], p->y + dy[i]);
	if(near[next.x][next.y] != -1){
	  near[next.x][next.y]++;
	  if(beach[next.x][next.y] - '0' <= near[next.x][next.y]){
	    broken2.push_back(next);
	    near[next.x][next.y] = -1;
	  }
	}
      }
    }
  }

  cout << ans << endl;
  return 0;
}

