#include <iostream>
#include <vector>
#include <string>
#include <queue>

using namespace std;

int main() {
    int h, w;
    cin >> h >> w;
    vector<string> ban(h);
    vector<vector<int>> count(h, vector<int>(w));
    for (int i = 0; i < h; i++) {
        cin >> ban[i];
        for (int j = 0; j < w; j++) {
            if (ban[i][j] != '.') {
                for (int k = -1; k <= 1; k++) {
                    for (int l = -1; l <= 1; l++) {
                        if (k == 0 && l == 0)continue;
                        count[i + k][j + l]++;
                    }
                }
            }
        }
    }
    queue<pair<int, int>> q;
    vector<vector<bool>> broken(h, vector<bool>(w));
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            if (ban[i][j] != '.') {
                if (ban[i][j] - '0' <= 8 - count[i][j]) {
                    q.push(make_pair(i, j));
                    broken[i][j] = true;
                }
            }
        }
    }
    q.push(make_pair(-1, -1));
    int ret = 0;
    while (true) {
        pair<int, int> x = q.front();
        if (x.first < 0) {
            q.pop();
            ret++;
            if (q.empty())break;
            q.push(make_pair(-1, -1));
        } else {
            q.pop();
            for (int k = -1; k <= 1; k++) {
                for (int l = -1; l <= 1; l++) {
                    if (k == 0 && l == 0)continue;
                    int a = x.first + k;
                    int b = x.second + l;
                    count[a][b]--;
                    bool flag=broken[a][b];
                    if (ban[a][b] != '.' && !flag) {
                        if (ban[a][b] - '0' <= 8 - count[a][b]) {
                            q.push(make_pair(a, b));
                            broken[a][b] = true;
                        }
                    }
                }
            }
        }
    }
    cout << ret << endl;
    return 0;
}

#include <bits/stdc++.h>

using namespace std;

int broken[1024][1024];
bool vis[1024][1024];
char board[1024][1024];

int countBroken(int y, int x, int lev)
{
    int ret = 0;

    for (int i = -1; i <= 1; i++){
        for (int j = -1; j <= 1; j++){
            if (i == 0 && j == 0) continue;
            ret += (~broken[y + i][x + j] && broken[y + i][x + j] <= lev);
        }
    }

    return (ret);
}

int main()
{
    int w, h;

    scanf("%d %d", &h, &w);

    memset(broken, -1, sizeof(broken));
    memset(board, '.', sizeof(board));

    int ans = 0;
    queue<pair<int, int> > q;

    for (int i = 1; i <= h; i++){
        scanf("%s", board[i] + 1);
        for (int j = 1; j <= w; j++){
            if (board[i][j] == '.'){
                broken[i][j] = 0;
                vis[i][j] = true;
                q.push(make_pair(i, j));
            }
            else board[i][j] -= '0';
        }
    }

    while (q.size()){
        pair<int, int> x = q.front(); q.pop();

        int Y = x.first, X = x.second;
        ans = max(ans, broken[Y][X]);

        for (int i = -1; i <= 1; i++){
            for (int j = -1; j <= 1; j++){
                if (i == 0 && j == 0) continue;
                if (!vis[Y + i][X + j] && countBroken(Y + i, X + j, broken[Y][X]) >= board[Y + i][X + j]){
                    broken[Y + i][X + j] = broken[Y][X] + 1;
                    vis[Y + i][X + j] = true;
                    q.push(make_pair(Y + i, X + j));
                }
            }
        }
    }

    printf("%d\n", ans);

    return (0);
}

