struct Castle {
	int h;
	int w;
	int times;
};
int main() {
	cin.tie(0); ios::sync_with_stdio(false);
	ll H, W; cin >> H >> W;
	vector<vector<char>> sand(H, vector<char>(W));
	vector<vector<int>> m(H, vector<int>(W, 0));
	queue<Castle> q;
	for (int i = 0; i < H;i++) for (int j = 0; j < W;j++) cin >> sand[i][j];
	for (int i = 0; i < H;i++) {
		for (int j = 0; j < W;j++) {
			if (sand[i][j] == '.') m[i][j] = INF;
