				for (int k = 0; k < 8;k++) {
					int vi = i + dx[k];
					int vj = j + dy[k];
					if (sand[vi][vj] == '.')m[i][j]++;
				}
				if (m[i][j] >= sand[i][j] - '0') {
					q.push({ i,j,1 });
				}
			}
		}
	}
	int ret = 0;
	while (!q.empty()) {
		Castle cas = q.front(); q.pop();
		ret = cas.times;
