int w,h;

char dat[1005][1005];


int gs(int y,int x){
	int res=0;
	reg(dy,-1,1){
		reg(dx,-1,1){
			res += (dat[y+dy][x+dx]=='.'?1:0);
		}
	}
	return res;
}

int main(void){
	scanf("%d%d",&h,&w);
	rep(y,h)scanf("%s",dat[y]);

	int ans=0;
	vector<mp> v[2];
	rep(y,h){
		rep(x,w){
			char c = dat[y][x];
			if(c=='.')continue;
			if(gs(y,x)>=(c-'0'))v[0].push_back(mp(y,x));
		}
	}

	for(;;){
		bool bd=false;

		rep(i,v[0].size()){
			mp pa=v[0][i];
			int y=pa.fir,x=pa.sec;
			if(dat[y][x]=='.')continue;
			dat[y][x]='.';
			bd=true;

			reg(dy,-1,1){
				reg(dx,-1,1){
					char c = dat[y+dy][x+dx];
					if(gs(y+dy,x+dx)>=(c-'0'))v[1].push_back(mp(y+dy,x+dx));
				}
			}
		}

		swap(v[0],v[1]);
		v[1].clear();
		if(!bd)break;
		ans++;
	}
	printf("%d\n",ans);
	return 0;
}

