		if(str[i][j] != '.')
		a[i+1][j+1] = str[i][j]-'0';
	}

	rrep(i,h)rrep(j,w)if(a[i][j] == inf)calc(i, j);
	printf("%d\n", solve(0));

	/*for(int i = 1;i <= h;i++){
		for(int j = 1;j <= w;j++)printf("%d ", a[i][j]);
		printf("\n");
	}*/
	return 0;
}

