#include <iostream>
#include <queue>
#include <utility>
#include <cstring>

using namespace std;

int h,w;
char bch[1003][1003];
bool done[1003][1003];

int wav (int i, int j)
{
    int c=0;
    if (bch[i-1][j-1]=='.') c++;
    if (bch[i-1][j]=='.') c++;
    if (bch[i-1][j+1]=='.') c++;
    if (bch[i][j-1]=='.') c++;
    if (bch[i][j+1]=='.') c++;
    if (bch[i+1][j-1]=='.') c++;
    if (bch[i+1][j]=='.') c++;
    if (bch[i+1][j+1]=='.') c++;
    return c;
}

int bfs ()
{
    int cnt, ans=0;
    bool ok=true;
    queue<pair<int, int>> que;
    vector<pair<int, int>> vec;
    for (int i=1; i<h-1; i++)
    {
        for (int j=1; j<w-1; j++)
        {
            if (bch[i][j]=='.') continue;
            cnt=wav(i, j);
            if (cnt>=bch[i][j]-'0') que.push({i, j});
        }
    }
    while (1)
    {
        if (que.empty())
        {
            if (!ok) break;
            ans++;
            ok=false;
            for (auto u : vec)
            {
                int i=u.first, j=u.second;
                bch[i][j]='.';
            }
            memset(done, false, sizeof(done));
            for (auto u : vec)
            {
                int i=u.first, j=u.second;
                if (bch[i-1][j-1]!='.' && !done[i-1][j-1]) { done[i-1][j-1]=true; que.push({i-1, j-1}); }
                if (bch[i-1][j]!='.' && !done[i-1][j]) { done[i-1][j]=true; que.push({i-1, j}); }
                if (bch[i-1][j+1]!='.' && !done[i-1][j+1]) { done[i-1][j+1]=true; que.push({i-1, j+1}); }
                if (bch[i][j-1]!='.' && !done[i][j-1]) { done[i][j-1]=true; que.push({i, j-1}); }
                if (bch[i][j+1]!='.' && !done[i][j+1]) { done[i][j+1]=true; que.push({i, j+1}); }
                if (bch[i+1][j-1]!='.' && !done[i+1][j-1]) { done[i+1][j-1]=true; que.push({i+1, j-1}); }
                if (bch[i+1][j]!='.' && !done[i+1][j]) { done[i+1][j]=true; que.push({i+1, j}); }
                if (bch[i+1][j+1]!='.' && !done[i+1][j+1]) { done[i+1][j+1]=true; que.push({i+1, j+1}); }
            }
            vec.clear();
        }
        if (que.empty()) break;
        int b=que.front().first, c=que.front().second;
        que.pop();
        if (bch[b][c]=='.') continue;
        cnt=wav(b, c);
        if (cnt<bch[b][c]-'0') continue;
        vec.push_back({b,c});
        ok=true;
    }
    return ans;
}

int main()
{
    cin>>h>>w;
    for (int i=0; i<h; i++) for (int j=0; j<w; j++) cin>>bch[i][j];
    cout<<bfs()<<'\n';
    return 0;
}

